// ==========================================
// ARCHIVO: frontend/src/App.jsx
// ==========================================
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { TemaProvider } from './contexto/ContextoTema';
import { AuthProvider } from './contexto/ContextoAuth';
import { CarritoProvider } from './contexto/ContextoCarrito';
import { NotificacionProvider } from './contexto/ContextoNotificacion';

// Componentes de Layout
import Encabezado from './componentes/comun/Encabezado';
import BarraLateral from './componentes/comun/BarraLateral';
import PiePagina from './componentes/comun/PiePagina';

// P√°ginas principales
import PaginaPrincipal from './paginas/PaginaPrincipal';
import PaginaTablero from './paginas/PaginaTablero';

// Autenticaci√≥n
import IniciarSesion from './componentes/autenticacion/IniciarSesion';
import Registrarse from './componentes/autenticacion/Registrarse';

// Hook personalizado para verificar autenticaci√≥n
import { useAuth } from './contexto/ContextoAuth';

// Componente para rutas protegidas
const RutaProtegida = ({ children }) => {
  const { usuario, cargando } = useAuth();
  
  if (cargando) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }
  
  return usuario ? children : <Navigate to="/iniciar-sesion" />;
};

// Componente principal de la aplicaci√≥n
const ContenidoPrincipal = () => {
  const { usuario } = useAuth();

  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <Routes>
          {/* Rutas p√∫blicas */}
          <Route path="/" element={
            <div>
              <Encabezado />
              <PaginaPrincipal />
              <PiePagina />
            </div>
          } />
          
          <Route path="/iniciar-sesion" element={
            usuario ? <Navigate to="/tablero" /> : <IniciarSesion />
          } />
          
          <Route path="/registrarse" element={
            usuario ? <Navigate to="/tablero" /> : <Registrarse />
          } />

          {/* Rutas protegidas */}
          <Route path="/tablero" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50">
                    <PaginaTablero />
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          {/* Otras rutas administrativas */}
          <Route path="/clientes" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üë•</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Gesti√≥n de Clientes</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          <Route path="/proveedores" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üè≠</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Gesti√≥n de Proveedores</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          <Route path="/productos" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üì¶</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Gesti√≥n de Productos</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          <Route path="/inventario" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üìä</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Control de Inventario</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          <Route path="/pedidos" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üìã</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Gesti√≥n de Pedidos</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          <Route path="/facturas" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üßæ</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Facturaci√≥n</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          <Route path="/pagos" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üí≥</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Gesti√≥n de Pagos</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          <Route path="/descuentos" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üè∑Ô∏è</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Descuentos y Promociones</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          <Route path="/integraciones" element={
            <RutaProtegida>
              <div className="flex h-screen bg-gray-100">
                <BarraLateral />
                <div className="flex-1 flex flex-col overflow-hidden">
                  <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50 p-6">
                    <div className="text-center py-12">
                      <div className="text-6xl mb-4">üîó</div>
                      <h2 className="text-2xl font-bold text-gray-900 mb-2">Integraciones</h2>
                      <p className="text-gray-600">M√≥dulo en desarrollo</p>
                    </div>
                  </main>
                </div>
              </div>
            </RutaProtegida>
          } />

          {/* Ruta por defecto */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
};

// Componente principal de la aplicaci√≥n
const App = () => {
  return (
    <TemaProvider>
      <AuthProvider>
        <CarritoProvider>
          <NotificacionProvider>
            <ContenidoPrincipal />
          </NotificacionProvider>
        </CarritoProvider>
      </AuthProvider>
    </TemaProvider>
  );
};

export default App;
