// ==========================================
// ARCHIVO: frontend/src/App.jsx
// ==========================================
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { TemaProvider } from './contexto/ContextoTema';
import { AuthProvider } from './contexto/ContextoAuth';
import { CarritoProvider } from './contexto/ContextoCarrito';
import { NotificacionProvider } from './contexto/ContextoNotificacion';

// Componentes de Layout
import Encabezado from './componentes/comun/Encabezado';
import EncabezadoAdmin from './componentes/comun/EncabezadoAdmin';
import BarraLateral from './componentes/comun/BarraLateral';
import PiePagina from './componentes/comun/PiePagina';


// Páginas principales
import PaginaInicio from './paginas/PaginaInicio';  // NUEVA - Landing Page
import PaginaPrincipal from './paginas/PaginaPrincipal';  // Catálogo/Ofertas
import PaginaTablero from './paginas/PaginaTablero';
import PanelCliente from './paginas/PanelCliente';
import Herramientas from './paginas/Herramientas';
import Construccion from './paginas/Construccion';
import Seguridad from './paginas/Seguridad';
import Contacto from './paginas/Contacto';
import Ofertas from './paginas/Ofertas';
import ConfirmacionPago from './paginas/ConfirmacionPago';
import PagoExitoso from './paginas/PagoExitoso';
import PaginaClientes from './paginas/PaginaClientes';
import PaginaInventario from './paginas/PaginaInventario';
import PaginaIntegraciones from './paginas/PaginaIntegraciones';
import PaginaProductos from './paginas/PaginaProductos';
import PaginaPedidos from './paginas/PaginaPedidos';
import PaginaFacturas from './paginas/PaginaFacturas';
import PaginaPagos from './paginas/PaginaPagos';
import PaginaDescuentos from './paginas/PaginaDescuentos';
import PaginaCupones from './paginas/PaginaCupones';
import PaginaPerfil from './paginas/PaginaPerfil';
import PaginaCompras from './paginas/PaginaCompras';

// Autenticación
import IniciarSesion from './componentes/autenticacion/IniciarSesion';
import Registrarse from './componentes/autenticacion/Registrarse';

// Hook personalizado para verificar autenticación
import { useAuth } from './contexto/ContextoAuth';

import TestBackend from './componentes/TestBackend';
import DetalleProducto from './componentes/productos/DetalleProducto';
import DetalleCliente from './componentes/clientes/DetalleCliente';
import FormularioCliente from './componentes/clientes/FormularioCliente';

// Wrapper para cargar el producto por ID y pasarlo a DetalleProducto
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { servicioProductos } from './servicios/servicioProductos';

// Componente para rutas protegidas
const RutaProtegida = ({ children }) => {
  const { usuario, cargando } = useAuth();
  
  if (cargando) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Cargando...</p>
        </div>
      </div>
    );
  }
  
  return usuario ? children : <Navigate to="/iniciar-sesion" />;
};

// Layout para páginas públicas
const LayoutPublico = ({ children }) => {
  return (
    <div className="min-h-screen flex flex-col">
      <Encabezado />
      <main className="flex-1">
        {children}
      </main>
      <PiePagina />
    </div>
  );
};

// Layout para páginas administrativas
const LayoutAdmin = ({ children }) => {
  return (
    <div className="flex h-screen bg-gray-100">
      <BarraLateral />
      <div className="flex-1 flex flex-col overflow-hidden">
        <EncabezadoAdmin />
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50">
          {children}
        </main>
      </div>
    </div>
  );
};

// Componente para páginas en desarrollo
const PaginaEnDesarrollo = ({ titulo, icono }) => {
  return (
    <div className="p-6">
      <div className="text-center py-12">
        <div className="text-6xl mb-4">{icono}</div>
        <h2 className="text-2xl font-bold text-gray-900 mb-2">{titulo}</h2>
        <p className="text-gray-600 mb-6">Este módulo está en desarrollo</p>
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 max-w-md mx-auto">
          <p className="text-sm text-blue-800">
            Próximamente tendrás acceso a todas las funcionalidades de este módulo.
          </p>
        </div>
      </div>
    </div>
  );
};

// Wrapper para cargar el producto por ID y pasarlo a DetalleProducto
const DetalleProductoWrapper = () => {
  const { id } = useParams();
  const [producto, setProducto] = useState(null);
  const [cargando, setCargando] = useState(true);
  
  useEffect(() => {
    const cargar = async () => {
      setCargando(true);
      try {
        const res = await servicioProductos.obtenerPorId(id);
        setProducto(res.data);
      } catch (e) {
        setProducto(null);
      } finally {
        setCargando(false);
      }
    };
    cargar();
  }, [id]);
  
  if (cargando) return <div className="p-8 text-center">Cargando...</div>;
  return <DetalleProducto producto={producto} />;
};

// Componente principal de la aplicación
const ContenidoPrincipal = () => {
  const { usuario } = useAuth();

  return (
    <Router>
      <Routes>
        {/* PÁGINA DE INICIO - LANDING PAGE */}
        <Route 
          path="/" 
          element={
            <LayoutPublico>
              <PaginaInicio />
            </LayoutPublico>
          } 
        />

        {/* CATÁLOGO/OFERTAS - Tu página actual */}
        <Route 
          path="/catalogo" 
          element={
            <LayoutPublico>
              <PaginaPrincipal />
            </LayoutPublico>
          } 
        />

        {/* PRODUCTOS PÚBLICOS - Nueva ruta pública */}
        <Route 
          path="/productos" 
          element={
            <LayoutPublico>
              <PaginaPrincipal />
            </LayoutPublico>
          } 
        />

        {/* OFERTAS - Página independiente */}
        <Route 
          path="/ofertas" 
          element={
            <LayoutPublico>
              <Ofertas />
            </LayoutPublico>
          } 
        />

        {/* Rutas de autenticación */}
        <Route 
          path="/iniciar-sesion" 
          element={
            usuario ? <Navigate to="/tablero" /> : <IniciarSesion />
          } 
        />
        
        <Route 
          path="/registrarse" 
          element={
            usuario ? <Navigate to="/tablero" /> : <Registrarse />
          } 
        />

        {/* Panel de Cliente */}
        <Route 
          path="/mi-cuenta" 
          element={
            <RutaProtegida>
              <LayoutPublico>
                <PanelCliente />
              </LayoutPublico>
            </RutaProtegida>
          } 
        />

        {/* Perfil del Cliente */}
        <Route 
          path="/perfil" 
          element={
            <RutaProtegida>
              <LayoutPublico>
                <PaginaPerfil />
              </LayoutPublico>
            </RutaProtegida>
          } 
        />

        {/* DETALLE DE PRODUCTO - Página nueva */}
        <Route 
          path="/producto/:id" 
          element={
            <LayoutPublico>
              <DetalleProductoWrapper />
            </LayoutPublico>
          } 
        />

        {/* Detalle de Producto (formato plural) */}
        <Route 
          path="/productos/:id" 
          element={
            <LayoutPublico>
              <DetalleProductoWrapper />
            </LayoutPublico>
          } 
        />

        {/* Rutas administrativas protegidas */}
        <Route 
          path="/tablero" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaTablero />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gestión de Clientes */}
        <Route 
          path="/clientes" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaClientes />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        <Route 
          path="/clientes/nuevo" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <FormularioCliente />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        <Route 
          path="/clientes/editar/:id" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <FormularioCliente modoEdicion={true} />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        <Route 
          path="/clientes/:id" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <DetalleCliente />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gestión de Inventario */}
        <Route 
          path="/inventario" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaInventario />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gestión de Productos */}
        <Route 
          path="/admin/productos" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaProductos />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gestión de Integraciones */}
        <Route 
          path="/integraciones" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaIntegraciones />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gestión de Pedidos */}
        <Route 
          path="/pedidos" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaPedidos />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gestión de Facturas */}
        <Route 
          path="/facturas" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaFacturas />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gestión de Pagos */}
        <Route 
          path="/pagos" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaPagos />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gestión de Descuentos */}
        <Route 
          path="/descuentos" 
          element={
            <RutaProtegida>
              <LayoutAdmin>
                <PaginaDescuentos />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Páginas de pago */}
        <Route 
          path="/confirmacion-pago" 
          element={
            <RutaProtegida>
              <LayoutPublico>
                <ConfirmacionPago />
              </LayoutPublico>
            </RutaProtegida>
          } 
        />

        <Route 
          path="/pago-exitoso" 
          element={
            <LayoutPublico>
              <PagoExitoso />
            </LayoutPublico>
          } 
        />

        {/* Páginas de servicios */}
        <Route 
          path="/herramientas" 
          element={
            <LayoutPublico>
              <Herramientas />
            </LayoutPublico>
          } 
        />

        <Route 
          path="/construccion" 
          element={
            <LayoutPublico>
              <Construccion />
            </LayoutPublico>
          } 
        />

        <Route 
          path="/seguridad" 
          element={
            <LayoutPublico>
              <Seguridad />
            </LayoutPublico>
          } 
        />

        <Route 
          path="/contacto" 
          element={
            <LayoutPublico>
              <Contacto />
            </LayoutPublico>
          } 
        />

        {/* Ruta de prueba del backend */}
        <Route 
          path="/test-backend" 
          element={<TestBackend />} 
        />

        {/* Página de cupones (admin) */}
        <Route 
          path="/cupones" 
          element={
            <LayoutAdmin>
              <PaginaCupones />
            </LayoutAdmin>
          } 
        />

        {/* Mis Compras */}
        <Route 
          path="/compras" 
          element={
            <RutaProtegida>
              <LayoutPublico>
                <PaginaCompras />
              </LayoutPublico>
            </RutaProtegida>
          } 
        />

        {/* Ruta 404 */}
        <Route 
          path="*" 
          element={
            <LayoutPublico>
              <div className="min-h-screen flex items-center justify-center">
                <div className="text-center">
                  <h1 className="text-6xl font-bold text-gray-900 mb-4">404</h1>
                  <p className="text-xl text-gray-600 mb-8">Página no encontrada</p>
                  <a 
                    href="/" 
                    className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors"
                  >
                    Volver al inicio
                  </a>
                </div>
              </div>
            </LayoutPublico>
          } 
        />
      </Routes>
    </Router>
  );
};

// Componente principal con providers
const App = () => {
  return (
    <TemaProvider>
      <AuthProvider>
        <CarritoProvider>
          <NotificacionProvider>
            <ContenidoPrincipal />
          </NotificacionProvider>
        </CarritoProvider>
      </AuthProvider>
    </TemaProvider>
  );
};

export default App;
