import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  MapPinIcon,
  CreditCardIcon,
  ShoppingCartIcon,
  TruckIcon,
  CheckIcon,
  XMarkIcon,
  EyeIcon,
  EyeSlashIcon
} from '@heroicons/react/24/outline';
import { useCarrito } from '../contexto/ContextoCarrito';
import { useAuth } from '../contexto/ContextoAuth';
import { servicioPagos } from '../servicios/servicioPagos';

const DATOS_BANCARIOS = {
  banco: 'Banco de Prueba',
  titular: 'Ferremas SpA',
  rut: '76.123.456-7',
  tipoCuenta: 'Cuenta Corriente',
  numeroCuenta: '123456789',
  email: 'pagos@ferremas.cl',
};

const ConfirmacionPago = () => {
  const navigate = useNavigate();
  const { carrito, limpiarCarrito } = useCarrito();
  const { usuario } = useAuth();

  const [formData, setFormData] = useState({
    nombre: usuario?.nombre || '',
    email: usuario?.email || '',
    telefono: usuario?.telefono || '',
    direccion: '',
    ciudad: 'Santiago',
    comuna: '',
    codigoPostal: ''
  });
  const [metodoEntrega, setMetodoEntrega] = useState('retiro');
  const [metodoPago, setMetodoPago] = useState('webpay');
  const [cargando, setCargando] = useState(false);
  const [error, setError] = useState('');
  const [exito, setExito] = useState(false);
  const [cupon, setCupon] = useState('');
  const [mostrarCupon, setMostrarCupon] = useState(false);
  const [cuponAplicado, setCuponAplicado] = useState(null);
  const [descuentoCupon, setDescuentoCupon] = useState(0);

  // Calcular totales
  const subtotal = carrito.reduce((total, item) => total + (item.precio * item.cantidad), 0);
  const iva = subtotal * 0.19;
  const costoDespacho = metodoEntrega === 'despacho' ? 5000 : 0;
  const total = subtotal + iva + costoDespacho;

  // Buscar cupÃ³n aplicado y descuento en localStorage (ajusta segÃºn tu lÃ³gica real)
  const cuponAplicadoLocal = localStorage.getItem('cupon_aplicado');
  const descuentoCuponLocal = localStorage.getItem('descuento_cupon');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setCargando(true);
    setError('');

    if (metodoPago === 'webpay') {
      try {
        // Crear transacciÃ³n con Transbank
        const transaccionData = {
          monto: Math.round(total),
          ordenCompra: `ORD-${Date.now()}`,
          sessionId: `session-${Date.now()}`,
          returnUrl: `${window.location.origin}/pago-exitoso`,
          items: carrito.map(item => ({
            id: item.id,
            nombre: item.nombre,
            cantidad: item.cantidad,
            precio: item.precio
          })),
          cliente: {
            id: usuario?.id,
            nombre: formData.nombre,
            email: formData.email,
            telefono: formData.telefono,
            direccion: formData.direccion,
            ciudad: formData.ciudad,
            comuna: formData.comuna
          },
          metodoEntrega,
          metodoPago
        };
        
        console.log('ðŸ”„ Enviando datos de transacciÃ³n:', transaccionData);
        
        const response = await servicioPagos.crearTransaccionTransbank(transaccionData);
        
        console.log('ðŸ“¬ Respuesta de Transbank:', response);
        
        if (response.success && response.message && response.message.url && response.message.token) {
          // RedirecciÃ³n segura a Webpay usando formulario POST
          const form = document.createElement('form');
          form.method = 'POST';
          form.action = response.message.url;

          const input = document.createElement('input');
          input.type = 'hidden';
          input.name = 'token_ws';
          input.value = response.message.token;
          form.appendChild(input);

          document.body.appendChild(form);
          form.submit();
        } else {
          console.error('âŒ Respuesta invÃ¡lida de Transbank:', response);
          setError(`Error al procesar el pago: ${response.message || 'Respuesta invÃ¡lida del servidor'}`);
        }
      } catch (error) {
        console.error('âŒ Error al crear transacciÃ³n:', error);
        setError(`Error al procesar el pago: ${error.message || 'Error de conexiÃ³n'}`);
      } finally {
        setCargando(false);
      }
    } else {
      // Simular registro de pedido para transferencia o efectivo
      setTimeout(() => {
        setExito(true);
        limpiarCarrito();
        setCargando(false);
      }, 1200);
    }
  };

  const aplicarCupon = async () => {
    if (!cupon.trim()) return setError('Ingresa un cÃ³digo de cupÃ³n');
    try {
      const res = await fetch(`/api/v1/promociones/cupones/validar?codigo=${encodeURIComponent(cupon)}`);
      const data = await res.json();
      if (!data.success) return setError(data.message || 'CupÃ³n invÃ¡lido');
      setCuponAplicado(data.cupon);
      setDescuentoCupon(data.descuento);
      setError('');
    } catch (err) {
      setError('Error al validar el cupÃ³n');
    }
  };

  if (carrito.length === 0 && !exito) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <ShoppingCartIcon className="h-16 w-16 text-gray-400 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Carrito VacÃ­o</h2>
          <p className="text-gray-600 mb-4">No hay productos en tu carrito para procesar el pago.</p>
          <button
            onClick={() => navigate('/catalogo')}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
          >
            Ir al CatÃ¡logo
          </button>
        </div>
      </div>
    );
  }

  if (exito) {
    return (
      <div className="min-h-screen bg-green-50 flex items-center justify-center">
        <div className="bg-white rounded-lg shadow-lg p-8 text-center max-w-md mx-auto">
          <CheckIcon className="h-16 w-16 text-green-500 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-green-700 mb-2">Â¡Pedido registrado!</h2>
          <p className="text-gray-700 mb-4">
            Tu pedido ha sido registrado correctamente.<br />
            {metodoPago === 'transferencia' && (
              <>Por favor realiza la transferencia a la cuenta indicada y envÃ­a el comprobante a <b>{DATOS_BANCARIOS.email}</b>.</>
            )}
            {metodoPago === 'efectivo' && (
              <>Recuerda que debes pagar en efectivo al momento de retirar tu pedido en tienda.</>
            )}
          </p>
          <button
            onClick={() => navigate('/')}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
          >
            Volver al inicio
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white rounded-lg shadow-lg overflow-hidden"
        >
          {/* Header */}
          <div className="bg-blue-600 text-white p-6">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-2xl font-bold">Confirmar Pedido</h1>
