// ==========================================
// ARCHIVO: frontend/src/App.jsx
// ==========================================
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { TemaProvider } from './contexto/ContextoTema';
import { AuthProvider } from './contexto/ContextoAuth';
import { CarritoProvider } from './contexto/ContextoCarrito';
import { NotificacionProvider } from './contexto/ContextoNotificacion';

// Componentes de Layout
import Encabezado from './componentes/comun/Encabezado';
import EncabezadoAdmin from './componentes/comun/EncabezadoAdmin';
import BarraLateral from './componentes/comun/BarraLateral';
import PiePagina from './componentes/comun/PiePagina';

// P谩ginas principales
import PaginaInicio from './paginas/PaginaInicio';  // NUEVA: P谩gina de inicio/landing
import PaginaPrincipal from './paginas/PaginaPrincipal';  // AHORA: Cat谩logo/Ofertas
import PaginaTablero from './paginas/PaginaTablero';
import PanelCliente from './paginas/PanelCliente';

// Autenticaci贸n
import IniciarSesion from './componentes/autenticacion/IniciarSesion';
import Registrarse from './componentes/autenticacion/Registrarse';

// Hook personalizado para verificar autenticaci贸n
import { useAuth } from './contexto/ContextoAuth';

import TestBackend from './componentes/TestBackend';
import DetalleProducto from './componentes/productos/DetalleProducto';

// Wrapper para cargar el producto por ID y pasarlo a DetalleProducto
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { servicioProductos } from './servicios/servicioProductos';

// Componente para rutas protegidas
const RutaProtegida = ({ children, requiereAdmin = false }) => {
  const { usuario, cargando } = useAuth();
  
  if (cargando) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Cargando...</p>
        </div>
      </div>
    );
  }
  
  if (!usuario) {
    return <Navigate to="/iniciar-sesion" />;
  }
  
  if (requiereAdmin && usuario?.rol !== 'admin') {
    return <Navigate to="/" replace />;
  }
  
  return children;
};

// Layout para p谩ginas p煤blicas (con header y footer)
const LayoutPublico = ({ children }) => {
  return (
    <div className="min-h-screen flex flex-col">
      <Encabezado />
      <main className="flex-1">
        {children}
      </main>
      <PiePagina />
    </div>
  );
};

// Layout para p谩ginas administrativas
const LayoutAdmin = ({ children }) => {
  return (
    <div className="flex h-screen bg-gray-100">
      <BarraLateral />
      <div className="flex-1 flex flex-col overflow-hidden">
        <EncabezadoAdmin />
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-50">
          {children}
        </main>
      </div>
    </div>
  );
};

// Layout para panel de cliente
const LayoutCliente = ({ children }) => {
  return (
    <div className="min-h-screen flex flex-col">
      <Encabezado />
      <main className="flex-1 bg-gray-50">
        {children}
      </main>
      <PiePagina />
    </div>
  );
};

// Componente para p谩ginas en desarrollo
const PaginaEnDesarrollo = ({ titulo, icono }) => {
  return (
    <div className="p-6">
      <div className="text-center py-12">
        <div className="text-6xl mb-4">{icono}</div>
        <h2 className="text-2xl font-bold text-gray-900 mb-2">{titulo}</h2>
        <p className="text-gray-600 mb-6">Este m贸dulo est谩 en desarrollo</p>
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 max-w-md mx-auto">
          <p className="text-sm text-blue-800">
            Pr贸ximamente tendr谩s acceso a todas las funcionalidades de este m贸dulo.
          </p>
        </div>
      </div>
    </div>
  );
};

// Wrapper para cargar el producto por ID y pasarlo a DetalleProducto
const DetalleProductoWrapper = () => {
  const { id } = useParams();
  const [producto, setProducto] = useState(null);
  const [cargando, setCargando] = useState(true);
  
  useEffect(() => {
    const cargar = async () => {
      setCargando(true);
      try {
        const res = await servicioProductos.obtenerPorId(id);
        setProducto(res.data);
      } catch (e) {
        setProducto(null);
      } finally {
        setCargando(false);
      }
    };
    cargar();
  }, [id]);
  
  if (cargando) return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
        <p className="text-gray-600">Cargando producto...</p>
      </div>
    </div>
  );
  
  return <DetalleProducto producto={producto} />;
};

// Componente principal de la aplicaci贸n
const ContenidoPrincipal = () => {
  const { usuario } = useAuth();

  return (
    <Router>
      <Routes>
        {/* NUEVA ESTRUCTURA DE RUTAS */}
        
        {/* P谩gina de Inicio - Landing Page */}
        <Route 
          path="/" 
          element={
            <LayoutPublico>
              <PaginaInicio />
            </LayoutPublico>
          } 
        />

        {/* Cat谩logo/Ofertas - Tu p谩gina actual de productos */}
        <Route 
          path="/catalogo" 
          element={
            <LayoutPublico>
              <PaginaPrincipal />
            </LayoutPublico>
          } 
        />

        {/* Rutas de autenticaci贸n */}
        <Route 
          path="/iniciar-sesion" 
          element={
            usuario ? <Navigate to="/tablero" /> : <IniciarSesion />
          } 
        />
        
        <Route 
          path="/registrarse" 
          element={
            usuario ? <Navigate to="/tablero" /> : <Registrarse />
          } 
        />

        {/* Panel de Cliente */}
        <Route 
          path="/mi-cuenta" 
          element={
            <RutaProtegida>
              <LayoutCliente>
                <PanelCliente />
              </LayoutCliente>
            </RutaProtegida>
          } 
        />

        {/* Detalle de Producto */}
        <Route 
          path="/producto/:id" 
          element={
            <LayoutPublico>
              <DetalleProductoWrapper />
            </LayoutPublico>
          } 
        />

        {/* Test Backend */}
        <Route 
          path="/test-backend" 
          element={
            <LayoutPublico>
              <div className="container mx-auto px-4 py-8">
                <TestBackend />
              </div>
            </LayoutPublico>
          } 
        />

        {/* RUTAS ADMINISTRATIVAS */}
        
        {/* Dashboard Principal */}
        <Route 
          path="/tablero" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaTablero />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gesti贸n de Clientes */}
        <Route 
          path="/clientes" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Gesti贸n de Clientes" 
                  icono="" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gesti贸n de Proveedores */}
        <Route 
          path="/proveedores" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Gesti贸n de Proveedores" 
                  icono="" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gesti贸n de Productos */}
        <Route 
          path="/productos" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Gesti贸n de Productos" 
                  icono="" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Control de Inventario */}
        <Route 
          path="/inventario" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Control de Inventario" 
                  icono="" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gesti贸n de Pedidos */}
        <Route 
          path="/pedidos" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Gesti贸n de Pedidos" 
                  icono="" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Facturaci贸n */}
        <Route 
          path="/facturas" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Facturaci贸n" 
                  icono="Ь" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Gesti贸n de Pagos */}
        <Route 
          path="/pagos" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Gesti贸n de Pagos" 
                  icono="" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Descuentos y Promociones */}
        <Route 
          path="/descuentos" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Descuentos y Promociones" 
                  icono="凤" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Reportes y An谩lisis */}
        <Route 
          path="/reportes" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Reportes y An谩lisis" 
                  icono="" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Integraciones y APIs */}
        <Route 
          path="/integraciones" 
          element={
            <RutaProtegida requiereAdmin={true}>
              <LayoutAdmin>
                <PaginaEnDesarrollo 
                  titulo="Integraciones y APIs" 
                  icono="" 
                />
              </LayoutAdmin>
            </RutaProtegida>
          } 
        />

        {/* Ruta 404 - Redirecci贸n por defecto */}
        <Route 
          path="*" 
          element={<Navigate to="/" replace />} 
        />
      </Routes>
    </Router>
  );
};

// Componente principal de la aplicaci贸n
const App = () => {
  return (
    <TemaProvider>
      <AuthProvider>
        <CarritoProvider>
          <NotificacionProvider>
            <ContenidoPrincipal />
          </NotificacionProvider>
        </CarritoProvider>
      </AuthProvider>
    </TemaProvider>
  );
};

export default App;
