import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Bars3Icon,
  BellIcon,
  MagnifyingGlassIcon,
  UserCircleIcon,
  ShoppingCartIcon,
  SunIcon,
  MoonIcon,
  Cog6ToothIcon,
  ArrowRightOnRectangleIcon,
  ChevronDownIcon,
} from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import { useCarrito } from '../../hooks/useCarrito';
import { useTema } from '../../hooks/useTema';
import { useNotificacion } from '../../hooks/useNotificacion';

const Encabezado = ({ toggleSidebar }) => {
  const navigate = useNavigate();
  const { usuario, cerrarSesion } = useAuth();
  const { itemsCarrito, totalItems } = useCarrito();
  const { tema, alternarTema } = useTema();
  const { notificaciones, marcarComoLeida } = useNotificacion();
  
  const [busqueda, setBusqueda] = useState('');
  const [menuUsuarioAbierto, setMenuUsuarioAbierto] = useState(false);
  const [menuNotificacionesAbierto, setMenuNotificacionesAbierto] = useState(false);
  const [carritoAbierto, setCarritoAbierto] = useState(false);
  
  const menuUsuarioRef = useRef(null);
  const menuNotificacionesRef = useRef(null);
  const carritoRef = useRef(null);

  // Cerrar menús al hacer clic fuera
  useEffect(() => {
    const manejarClickFuera = (evento) => {
      if (menuUsuarioRef.current && !menuUsuarioRef.current.contains(evento.target)) {
        setMenuUsuarioAbierto(false);
      }
      if (menuNotificacionesRef.current && !menuNotificacionesRef.current.contains(evento.target)) {
        setMenuNotificacionesAbierto(false);
      }
      if (carritoRef.current && !carritoRef.current.contains(evento.target)) {
        setCarritoAbierto(false);
      }
    };

    document.addEventListener('mousedown', manejarClickFuera);
    return () => document.removeEventListener('mousedown', manejarClickFuera);
  }, []);

  const manejarBusqueda = (e) => {
    e.preventDefault();
    if (busqueda.trim()) {
      navigate(`/productos?busqueda=${encodeURIComponent(busqueda.trim())}`);
      setBusqueda('');
    }
  };

  const manejarCerrarSesion = async () => {
    await cerrarSesion();
    navigate('/iniciar-sesion');
  };

  const notificacionesNoLeidas = notificaciones?.filter(n => !n.leida) || [];

  return (
    <motion.header
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      className="bg-white dark:bg-gray-800 shadow-lg border-b border-gray-200 dark:border-gray-700 sticky top-0 z-50"
    >
      <div className="px-6 py-4">
        <div className="flex items-center justify-between">
          {/* Lado Izquierdo - Logo y Menú */}
          <div className="flex items-center space-x-4">
            {/* Botón del Menú */}
            <button
              onClick={toggleSidebar}
              className="p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700 transition-colors duration-200"
            >
              <Bars3Icon className="w-6 h-6" />
            </button>

            {/* Logo */}
            <Link to="/tablero" className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-blue-600 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-sm">F</span>
              </div>
              <span className="text-xl font-bold text-gray-900 dark:text-white">
                FERREMAS
              </span>
            </Link>
          </div>

          {/* Centro - Barra de Búsqueda */}
          <div className="flex-1 max-w-2xl mx-8">
            <form onSubmit={manejarBusqueda} className="relative">
              <div className="relative">
                <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                <input
                  type="text"
                  placeholder="Buscar productos, clientes, pedidos..."
                  value={busqueda}
                  onChange={(e) => setBusqueda(e.target.value)}
                  className="w-full pl-10 pr-4 py-2.5 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent dark:text-white dark:placeholder-gray-400 transition-all duration-200"
                />
              </div>
            </form>
          </div>

          {/* Lado Derecho - Acciones */}
          <div className="flex items-center space-x-4">
            {/* Cambiar Tema */}
            <button
              onClick={alternarTema}
              className="p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700 transition-colors duration-200"
            >
              {tema === 'dark' ? (
                <SunIcon className="w-6 h-6" />
              ) : (
                <MoonIcon className="w-6 h-6" />
              )}
            </button>

            {/* Carrito de Compras */}
            <div className="relative" ref={carritoRef}>
              <button
                onClick={() => setCarritoAbierto(!carritoAbierto)}
                className="p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700 transition-colors duration-200 relative"
              >
                <ShoppingCartIcon className="w-6 h-6" />
                {totalItems > 0 && (
                  <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                    {totalItems}
                  </span>
                )}
              </button>

              {/* Dropdown del Carrito */}
              <AnimatePresence>
                {carritoAbierto && (
                  <motion.div
                    initial={{ opacity: 0, scale: 0.95, y: -10 }}
                    animate={{ opacity: 1, scale: 1, y: 0 }}
                    exit={{ opacity: 0, scale: 0.95, y: -10 }}
                    transition={{ duration: 0.2 }}
                    className="absolute right-0 mt-2 w-80 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-50"
                  >
                    <div className="p-4">
                      <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-3">
                        Carrito de Compras
                      </h3>
                      {itemsCarrito.length === 0 ? (
                        <p className="text-gray-500 dark:text-gray-400 text-center py-4">
                          Tu carrito está vacío
                        </p>
                      ) : (
                        <div className="space-y-3">
                          {itemsCarrito.slice(0, 3).map((item) => (
                            <div key={item.id} className="flex items-center space-x-3">
                              <img
                                src={item.imagen || '/placeholder-product.jpg'}
                                alt={item.nombre}
                                className="w-12 h-12 object-cover rounded-lg"
                              />
                              <div className="flex-1">
                                <h4 className="text-sm font-medium text-gray-900 dark:text-white">
                                  {item.nombre}
                                </h4>
                                <p className="text-xs text-gray-500 dark:text-gray-400">
                                  Cantidad: {item.cantidad}
                                </p>
                              </div>
                              <span className="text-sm font-semibold text-gray-900 dark:text-white">
                                ${(item.precio * item.cantidad).toLocaleString()}
                              </span>
                            </div>
                          ))}
                          {itemsCarrito.length > 3 && (
                            <p className="text-xs text-gray-500 dark:text-gray-400 text-center">
                              +{itemsCarrito.length - 3} productos más
                            </p>
                          )}
                          <div className="border-t border-gray-200 dark:border-gray-600 pt-3">
                            <Link
                              to="/pedidos/carrito"
                              onClick={() => setCarritoAbierto(false)}
                              className="w-full btn btn-primary"
                            >
                              Ver Carrito Completo
                            </Link>
                          </div>
                        </div>
                      )}
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Notificaciones */}
            <div className="relative" ref={menuNotificacionesRef}>
              <button
                onClick={() => setMenuNotificacionesAbierto(!menuNotificacionesAbierto)}
                className="p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700 transition-colors duration-200 relative"
              >
                <BellIcon className="w-6 h-6" />
                {notificacionesNoLeidas.length > 0 && (
                  <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                    {notificacionesNoLeidas.length}
                  </span>
                )}
              </button>

              {/* Dropdown de Notificaciones */}
              <AnimatePresence>
                {menuNotificacionesAbierto && (
                  <motion.div
                    initial={{ opacity: 0, scale: 0.95, y: -10 }}
                    animate={{ opacity: 1, scale: 1, y: 0 }}
                    exit={{ opacity: 0, scale: 0.95, y: -10 }}
                    transition={{ duration: 0.2 }}
                    className="absolute right-0 mt-2 w-96 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-50"
                  >
                    <div className="p-4">
                      <div className="flex items-center justify-between mb-3">
                        <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                          Notificaciones
                        </h3>
                        {notificacionesNoLeidas.length > 0 && (
                          <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded-full">
                            {notificacionesNoLeidas.length} nuevas
                          </span>
                        )}
                      </div>
                      <div className="max-h-80 overflow-y-auto space-y-2">
                        {notificaciones?.length === 0 ? (
                          <p className="text-gray-500 dark:text-gray-400 text-center py-4">
                            No tienes notificaciones
                          </p>
                        ) : (
                          notificaciones?.slice(0, 5).map((notificacion) => (
                            <div
                              key={notificacion.id}
                              onClick={() => marcarComoLeida(notificacion.id)}
                              className={`p-3 rounded-lg cursor-pointer transition-colors duration-200 ${
                                notificacion.leida
                                  ? 'bg-gray-50 dark:bg-gray-700'
                                  : 'bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-700'
                              }`}
                            >
                              <h4 className="text-sm font-medium text-gray-900 dark:text-white">
                                {notificacion.titulo}
                              </h4>
                              <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                                {notificacion.mensaje}
                              </p>
                              <span className="text-xs text-gray-400 dark:text-gray-500">
                                {notificacion.fecha}
                              </span>
                            </div>
                          ))
                        )}
                      </div>
                      <div className="border-t border-gray-200 dark:border-gray-600 pt-3 mt-3">
                        <Link
                          to="/notificaciones"
                          onClick={() => setMenuNotificacionesAbierto(false)}
                          className="text-sm text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300"
                        >
                          Ver todas las notificaciones
                        </Link>
                      </div>
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Menú de Usuario */}
            <div className="relative" ref={menuUsuarioRef}>
              <button
                onClick={() => setMenuUsuarioAbierto(!menuUsuarioAbierto)}
                className="flex items-center space-x-3 p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700 transition-colors duration-200"
              >
                {usuario?.avatar ? (
                  <img
                    src={usuario.avatar}
                    alt={usuario.nombre}
                    className="w-8 h-8 rounded-full object-cover"
                  />
                ) : (
                  <UserCircleIcon className="w-8 h-8" />
                )}
                <div className="hidden md:block text-left">
                  <p className="text-sm font-medium text-gray-900 dark:text-white">
                    {usuario?.nombre || 'Usuario'}
                  </p>
                  <p className="text-xs text-gray-500 dark:text-gray-400">
                    {usuario?.rol || 'Empleado'}
                  </p>
                </div>
                <ChevronDownIcon className="w-4 h-4" />
              </button>

              {/* Dropdown del Usuario */}
              <AnimatePresence>
                {menuUsuarioAbierto && (
                  <motion.div
                    initial={{ opacity: 0, scale: 0.95, y: -10 }}
                    animate={{ opacity: 1, scale: 1, y: 0 }}
                    exit={{ opacity: 0, scale: 0.95, y: -10 }}
                    transition={{ duration: 0.2 }}
                    className="absolute right-0 mt-2 w-64 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-50"
                  >
                    <div className="p-4">
                      {/* Información del Usuario */}
                      <div className="border-b border-gray-200 dark:border-gray-600 pb-3 mb-3">
                        <div className="flex items-center space-x-3">
                          {usuario?.avatar ? (
                            <img
                              src={usuario.avatar}
                              alt={usuario.nombre}
                              className="w-12 h-12 rounded-full object-cover"
                            />
                          ) : (
                            <div className="w-12 h-12 rounded-full bg-gray-300 dark:bg-gray-600 flex items-center justify-center">
                              <UserCircleIcon className="w-8 h-8 text-gray-500 dark:text-gray-400" />
                            </div>
                          )}
                          <div>
                            <p className="text-sm font-medium text-gray-900 dark:text-white">
                              {usuario?.nombre || 'Usuario'}
                            </p>
                            <p className="text-xs text-gray-500 dark:text-gray-400">
                              {usuario?.email || 'usuario@ferremas.cl'}
                            </p>
                            <span className="inline-block mt-1 px-2 py-1 text-xs font-medium bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200 rounded-full">
                              {usuario?.rol || 'Empleado'}
                            </span>
                          </div>
                        </div>
                      </div>

                      {/* Opciones del Menú */}
                      <div className="space-y-1">
                        <Link
                          to="/perfil"
                          onClick={() => setMenuUsuarioAbierto(false)}
                          className="flex items-center space-x-3 w-full px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors duration-200"
                        >
                          <UserCircleIcon className="w-5 h-5" />
                          <span>Mi Perfil</span>
                        </Link>
                        
                        <Link
                          to="/configuracion"
                          onClick={() => setMenuUsuarioAbierto(false)}
                          className="flex items-center space-x-3 w-full px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors duration-200"
                        >
                          <Cog6ToothIcon className="w-5 h-5" />
                          <span>Configuración</span>
                        </Link>
                        
                        <div className="border-t border-gray-200 dark:border-gray-600 pt-1 mt-1">
                          <button
                            onClick={manejarCerrarSesion}
                            className="flex items-center space-x-3 w-full px-3 py-2 text-sm text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900/20 rounded-lg transition-colors duration-200"
                          >
                            <ArrowRightOnRectangleIcon className="w-5 h-5" />
                            <span>Cerrar Sesión</span>
                          </button>
                        </div>
                      </div>
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
          </div>
        </div>
      </div>
    </motion.header>
  );
};

export default Encabezado;
