import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { servicioProductos } from '../../servicios/servicioProductos';
import { TagIcon, PlusCircleIcon, PencilIcon, TrashIcon } from '@heroicons/react/24/outline';

const GestorPromociones = () => {
  const [productos, setProductos] = useState([]);
  const [promociones, setPromociones] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const cargarDatos = async () => {
      setCargando(true);
      setError(null);
      try {
        // Cargar todos los productos
        const resProductos = await servicioProductos.obtenerTodos({ limit: 1000 }); // Traer todos
        if (resProductos.success && Array.isArray(resProductos.data)) {
          setProductos(resProductos.data);
        } else {
          throw new Error('La respuesta de productos no es un array válido.');
        }

        // Aquí se cargarían las promociones existentes
        // Por ahora, usamos un array vacío.
        setPromociones([]);

      } catch (err) {
        setError(err.message || 'Error al cargar los datos');
        console.error(err);
      } finally {
        setCargando(false);
      }
    };

    cargarDatos();
  }, []);

  const formatearPrecio = (precio) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
    }).format(precio);
  };

  if (cargando) {
    return <div className="text-center p-8">Cargando productos y promociones...</div>;
  }

  if (error) {
    return <div className="text-center p-8 text-red-500">Error: {error}</div>;
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="space-y-6"
    >
      {/* Listado de Productos */}
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg">
        <div className="p-6 border-b border-gray-200 dark:border-gray-700">
          <h2 className="text-xl font-bold text-gray-900 dark:text-white">
            Productos
          </h2>
          <p className="text-gray-600 dark:text-gray-400 mt-1">
            Aplica descuentos a productos específicos.
          </p>
        </div>
        
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead className="bg-gray-50 dark:bg-gray-700">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Producto</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Precio Original</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Promoción Aplicada</th>
                <th scope="col" className="relative px-6 py-3">
                  <span className="sr-only">Acciones</span>
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200 dark:bg-gray-800">
              {productos.map((producto) => (
                <tr key={producto.id}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="flex-shrink-0 h-10 w-10">
                        <img className="h-10 w-10 rounded-full object-cover" src={producto.imagen || '/assets/imagenes/productos/placeholder.jpg'} alt={producto.nombre} />
                      </div>
                      <div className="ml-4">
                        <div className="text-sm font-medium text-gray-900 dark:text-white">{producto.nombre}</div>
                        <div className="text-sm text-gray-500 dark:text-gray-400">SKU: {producto.codigo_sku}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900 dark:text-white">{formatearPrecio(producto.precio)}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {producto.promocion ? (
                      <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                        {producto.promocion.nombre}
                      </span>
                    ) : (
                      <span className="text-sm text-gray-500 italic">Sin promoción</span>
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <button className="text-indigo-600 hover:text-indigo-900 dark:text-indigo-400 dark:hover:text-indigo-200 flex items-center">
                      <PlusCircleIcon className="w-5 h-5 mr-1" />
                      Crear Promoción
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          {productos.length === 0 && !cargando && (
            <div className="text-center py-12">
              <p className="text-gray-500">No se encontraron productos.</p>
            </div>
          )}
        </div>
      </div>
    </motion.div>
  );
};

export default GestorPromociones;
