import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  ShoppingBagIcon,
  PlusIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  EyeIcon,
  PencilIcon,
  TrashIcon,
  CheckCircleIcon,
  ClockIcon,
  TruckIcon,
  ExclamationTriangleIcon,
  XCircleIcon
} from '@heroicons/react/24/outline';
import { useNotificacion } from '../contexto/ContextoNotificacion';
import { servicioPedidos } from '../servicios/servicioPedidos';
import { servicioClientes } from '../servicios/servicioClientes';
import { servicioProductos } from '../servicios/servicioProductos';

const PaginaPedidos = () => {
  const [pedidos, setPedidos] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [pedidoSeleccionado, setPedidoSeleccionado] = useState(null);
  const [modalDetalle, setModalDetalle] = useState(false);
  const [modalEditar, setModalEditar] = useState(false);
  const [modalNuevo, setModalNuevo] = useState(false);
  const [formularioPedido, setFormularioPedido] = useState({
    cliente_id: '',
    productos: [],
    metodo_entrega: 'retiro_tienda',
    direccion_entrega: '',
    observaciones: ''
  });
  const [productosDisponibles, setProductosDisponibles] = useState([]);
  const [productoSeleccionado, setProductoSeleccionado] = useState({
    producto_id: '',
    cantidad: 1,
    precio_unitario: 0
  });
  const [filtros, setFiltros] = useState({
    estado: 'todos',
    fechaDesde: '',
    fechaHasta: '',
    busqueda: ''
  });
  const [clientes, setClientes] = useState([]);
  const [cargandoClientes, setCargandoClientes] = useState(true);
  const { exito, error } = useNotificacion();

  useEffect(() => {
    cargarPedidos();
    cargarProductos();
    cargarClientes();
  }, []);

  useEffect(() => {
    console.log('PRODUCTOS DISPONIBLES:', productosDisponibles);
  }, [productosDisponibles]);

  const cargarPedidos = async () => {
    setCargando(true);
    try {
      const response = await servicioPedidos.obtenerTodos();
      console.log('ðŸ“¦ Respuesta de pedidos:', response);
      if (response.success) {
        setPedidos(response.data.pedidos || []);
