// ==========================================
// ARCHIVO: frontend/src/servicios/api.js (SOLO BD REAL)
// ==========================================
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

// Funci√≥n base para hacer requests
const apiRequest = async (endpoint, options = {}) => {
  const url = `${API_BASE_URL}${endpoint}`;
  
  const config = {
    headers: {
      'Content-Type': 'application/json',
      ...options.headers,
    },
    ...options,
  };

  try {
    const response = await fetch(url, config);
    const data = await response.json();
    
    if (!response.ok) {
      throw new Error(data.message || `Error ${response.status}: ${response.statusText}`);
    }
    
    return data;
  } catch (error) {
    console.error(`Error en ${endpoint}:`, error);
    
    // Si es un error de red, mostrar mensaje m√°s amigable
    if (error.name === 'TypeError' && error.message.includes('fetch')) {
      throw new Error('No se pudo conectar con el servidor. Verifica que el backend est√© corriendo en http://localhost:3001');
    }
    
    throw error;
  }
};

// ==========================================
// API DE PRODUCTOS - SOLO BD REAL
// ==========================================
export const productosAPI = {
  // Obtener todos los productos REALES de tu BD
  obtenerTodos: async (filtros = {}) => {
    const params = new URLSearchParams();
    
    Object.keys(filtros).forEach(key => {
      if (filtros[key] !== '' && filtros[key] !== null && filtros[key] !== undefined) {
        params.append(key, filtros[key]);
      }
    });
    
    const queryString = params.toString();
    const endpoint = `/productos${queryString ? `?${queryString}` : ''}`;
    
    return await apiRequest(endpoint);
  },

  // Obtener producto espec√≠fico por ID
  obtenerPorId: async (id) => {
    if (!id) {
      throw new Error('ID del producto es requerido');
    }
    return await apiRequest(`/productos/${id}`);
  },

  // Crear nuevo producto en tu BD
  crear: async (producto) => {
    if (!producto.nombre || !producto.precio) {
      throw new Error('Nombre y precio son campos obligatorios');
    }
    
    return await apiRequest('/productos', {
      method: 'POST',
      body: JSON.stringify(producto),
    });
  },

  // Actualizar producto existente
  actualizar: async (id, producto) => {
    if (!id) {
      throw new Error('ID del producto es requerido');
    }
    
    return await apiRequest(`/productos/${id}`, {
      method: 'PUT',
      body: JSON.stringify(producto),
    });
  },

  // Eliminar producto (soft delete)
  eliminar: async (id) => {
    if (!id) {
      throw new Error('ID del producto es requerido');
    }
    
    return await apiRequest(`/productos/${id}`, {
      method: 'DELETE',
    });
  },

  // Obtener categor√≠as REALES de tu BD
  obtenerCategorias: async () => {
    return await apiRequest('/productos/categorias');
  },

  // Obtener marcas REALES de tu BD
  obtenerMarcas: async () => {
    return await apiRequest('/productos/marcas');
  },

  // Buscar productos con t√©rmino y filtros
  buscar: async (termino, filtros = {}) => {
    return await productosAPI.obtenerTodos({
      q: termino,
      ...filtros
    });
  },
};

// ==========================================
// API DEL DASHBOARD - SOLO ESTAD√çSTICAS REALES
// ==========================================
export const dashboardAPI = {
  // Obtener estad√≠sticas REALES de tu BD
  obtenerEstadisticas: async () => {
    return await apiRequest('/dashboard/stats');
  },
};

// ==========================================
// API DE SISTEMA - SOLO FUNCIONES B√ÅSICAS
// ==========================================
export const sistemaAPI = {
  // Verificar estado del servidor
  healthCheck: async () => {
    const response = await fetch(process.env.REACT_APP_API_URL?.replace('/api', '') || 'http://localhost:3001' + '/health');
    return await response.json();
  },
  
  // Test b√°sico de conectividad
  test: async () => {
    return await apiRequest('/test');
  },
  
  // Informaci√≥n del servidor
  infoServidor: async () => {
    const response = await fetch(process.env.REACT_APP_API_URL?.replace('/api', '') || 'http://localhost:3001');
    return await response.json();
  },
};

// ==========================================
// OBJETO PRINCIPAL DE LA API
// ==========================================
const api = {
  productos: productosAPI,
  dashboard: dashboardAPI,
  sistema: sistemaAPI,
};

// ==========================================
// FUNCI√ìN DE DIAGN√ìSTICO (SIN DEMOS)
// ==========================================
export const debugAPI = {
  // Probar conexi√≥n con la API real
  probarConexion: async () => {
    console.log('üß™ Probando conexi√≥n con la API...');
    
    const resultados = {
      servidor: null,
      productos: null,
      categorias: null,
      marcas: null,
      estadisticas: null,
    };
    
    try {
      // Test servidor
      console.log('1. Probando conexi√≥n al servidor...');
      try {
        const servidor = await sistemaAPI.test();
        resultados.servidor = { success: true, data: servidor };
      } catch (error) {
        resultados.servidor = { success: false, error: error.message };
      }
      
      // Test productos
      console.log('2. Probando endpoint de productos...');
      try {
        const productos = await productosAPI.obtenerTodos();
        resultados.productos = { success: true, total: productos.data?.length || 0 };
      } catch (error) {
        resultados.productos = { success: false, error: error.message };
      }
      
      // Test categor√≠as
      console.log('3. Probando endpoint de categor√≠as...');
      try {
        const categorias = await productosAPI.obtenerCategorias();
        resultados.categorias = { success: true, total: categorias.data?.length || 0 };
      } catch (error) {
        resultados.categorias = { success: false, error: error.message };
      }
      
      // Test marcas
      console.log('4. Probando endpoint de marcas...');
      try {
        const marcas = await productosAPI.obtenerMarcas();
        resultados.marcas = { success: true, total: marcas.data?.length || 0 };
      } catch (error) {
        resultados.marcas = { success: false, error: error.message };
      }
      
      // Test estad√≠sticas
      console.log('5. Probando endpoint de estad√≠sticas...');
      try {
        const stats = await dashboardAPI.obtenerEstadisticas();
        resultados.estadisticas = { success: true, data: stats.data };
      } catch (error) {
        resultados.estadisticas = { success: false, error: error.message };
      }
      
    } catch (error) {
      console.error('‚ùå Error general en las pruebas:', error);
    }
    
    console.log('üìä Resultados de las pruebas:', resultados);
    return resultados;
  },
};

// Exportaci√≥n por defecto
export default api;
